sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    const {
        params: {
            table,
            query,
            indicatorSysid,
            breakdowns,
            aggregate,
            domain,
            scoreDate
        },
        title
    } = event.payload;

    let payload;

    if (table) {
        payload = {
            fields: {
                table
            },
            params: {
                query,
            },
            route: 'simplelist',
            title: title || 'List'
        };
    } else {
        payload = {
            fields: {
                uuid: indicatorSysid,
            },
            params: {
                breakdowns: breakdowns,
                aggregate: aggregate,
                domain: domain,
                scoreDate: scoreDate,
            },
            route: 'ac_kpi_details',
            title: title || 'KPI Details'
        };

    }

    api.emit('NAV_ITEM_SELECTED', payload);
}
