/* Script to fix the record producer issue with Loader */

var gr = new GlideRecord('x_snc_dh_app_load_load_experience');
gr.get('9df8ed871b17ec1071fd8483cc4bcb3e');
var table = 'x_snc_dh_app_load_load_experience';
    var type = 'load';
    var load_flow_id = gr.load_subflow+'';
    var unload_flow_id = gr.unload_subflow+'';
    var loadAppUtils = new global.DHAppUtils();
    var is_load_invalid = loadAppUtils.validateVariables(load_flow_id);
    var is_unload_invalid = loadAppUtils.validateVariables(unload_flow_id);
    if(is_load_invalid == false && is_unload_invalid == false){
            loadAppUtils.createRecordProducer(gr.sys_id+'', gr.scope, table, type);
            loadAppUtils.syncLastUpdate(gr.sys_id+'', table, 'unload_subflow', 'unload_flow_last_updated');
            loadAppUtils.syncLastUpdate(gr.sys_id+'', table, 'load_subflow', 'load_flow_last_updated');
    }else{
        gs.addErrorMessage("The record producer hasn't been created, there is an unsupported Variable Type in the selected Flow");
    }
