sysauto_script.script:
/* Created by Shashank Tonpekar
   Created On: 28th Feb 2020
   Description: Script is to update incident record and set assignment group and assigned to on incidents
*/

// Get group memebers for default assignment groups
var SoftwareGrpMembers = [];
var GrpMemList = new GlideRecord('sys_user_grmember');
GrpMemList.addQuery('group=8a4dde73c6112278017a6a4baf547aa7');// group = Software
GrpMemList.query();
while (GrpMemList.next()) 
{
    SoftwareGrpMembers.push(GrpMemList.getValue('user')); // add the sysids to array

}


var IncList =  new GlideRecord('incident');
IncList.addEncodedQuery('active=true^assigned_toISEMPTY^assignment_group!=NULL');
IncList.query();
while (IncList.next())
{
    gs.info('Incident:' + IncList.getValue('number'));
    switch(IncList.getValue('assignment_group'))
    {
        case '8a4dde73c6112278017a6a4baf547aa7':
            var ranNum= Math.floor((Math.random() * SoftwareGrpMembers.length));
            IncList.setValue('assigned_to',SoftwareGrpMembers[ranNum]);
            gs.info('Software:Assigned to Set with value:'+ SoftwareGrpMembers[ranNum]);
            break;
        default:
                var GrpMembers = [];
                var GrpMemList = new GlideRecord('sys_user_grmember');
                GrpMemList.addQuery('group='+ IncList.getValue('assignment_group') );
                GrpMemList.query();
                while (GrpMemList.next()) 
                {
                    GrpMembers.push(GrpMemList.getValue('user')); // add the sysids to array

                }
                var ranNum= Math.floor((Math.random() * GrpMembers.length));
                IncList.setValue('assigned_to',GrpMembers[ranNum]);
                gs.info('Assigned to Set with value:'+ GrpMembers[ranNum]);


    }
    IncList.update();

} // end of while loop - incident processing
