sysauto_script.script:
var grProperty = new GlideRecord('sys_properties');
grProperty.addQuery('name', 'sn_app_ml_insights.chi_squared_critical_value');
grProperty.query();
if (grProperty.next()) {
    grProperty.setValue('value', '0.01');
    grProperty.update();
}

var recs = new GlideRecord('sysauto_script');
recs.addEncodedQuery('name=VMW Generate Scores and Execute Job - Demo Data');
recs.query();
if(recs.next()){
SncTriggerSynchronizer.executeNow(recs);
	gs.log('Executed 1st scheduled job - E2E Vendor management');
}
gs.sleep(900000);

var rec = new GlideRecord('sysauto_script');
rec.addEncodedQuery('name=Vendor Success Indicator Weekly Scheduled Job');
rec.query();
if(rec.next()){
SncTriggerSynchronizer.executeNow(rec);
	gs.log('Executed 2nd scheduled job - E2E Vendor management');
}
